//SPDX-License-Identifier: BUSL-1.1
pragma solidity ^0.8.0;

import "../interfaces/ISegaP.sol";
import "../interfaces/ISegaFactP.sol";
import "@openzeppelin/contracts/token/ERC20/IERC20.sol";

contract Vault {

    address public immutable segaFactory;
    address public immutable RCU;

    uint256 constant oneDay = 24*60*60 ; 
    //this is 1 for testing only, should be 24*60*60

    string constant noAccess = "User not Authorized"; 
    string constant txnFail = "Transaction Failed";
    string constant lowBalance = "Insufficient Balance";
    string constant badReceiver = "Receiver is not Owner or SEGA";

    address payable private owner;
    address private backupAccount;
    uint256 private releaseTime;
    uint256 private unlockDays;

    mapping (address => uint256) private SegaMap;
    address[] private SegaList;

    event Received(address, uint);
    event OwnerIsActive();

    event ChangedBackup(address _backup);
    event ChangedInactiveTime(uint);
    event OwnershipTransfered(address indexed _new, address indexed _old);

    event NewSegaCreated(address newSega);
    event SentOutAssets(address indexed _to, address indexed _token, uint256 _amount);

    event SegaFrozen(address indexed _sega,uint8 indexed onOff);
    event SegaRecall(address indexed _sega, address indexed _token, uint256 _amount);
    event SegaTraderChanged(address indexed _sega, address newTrader);

    /*********************************************
    Modifiers
    *********************************************/

    modifier isOwner() {
        _isOwner();
        _;
    }

    modifier isSega(address _sega) {
        _isSega(_sega);
        _;
    }

    modifier viewByORB() {
        _viewByORB();
        _;
    }

    function _isOwner() internal {
        require( msg.sender == owner, noAccess);
        ownerActive();
    }

    function _isSega(address _sega) internal view {
        require( SegaList[ SegaMap[_sega] ] != address(0), 
                "Sega Unkown to this Vault");
    }

    function ownerActive() internal {
        releaseTime = block.timestamp + unlockDays * oneDay;
        emit OwnerIsActive();
    }

    function _viewByORB() internal view {
        require(
            msg.sender == owner ||
            msg.sender == backupAccount,
            noAccess
        );
    }

    function _notZero(address _address) internal pure {
        require (_address != address(0), "Zero Address Not Allowed");
    }

    function _enoughBalance(IERC20 _token, uint256 _amount) internal view{
        require(_token.balanceOf(address(this)) >= _amount, lowBalance);
    }

    function _checkReceiver(address receiver) internal view {
        _notZero(receiver);
        require(receiver == owner ||
                SegaList[ SegaMap[receiver] ] != address(0), 
                badReceiver);
    }


    /*******************************************
    Constructor, Recieve, Fallback
    *******************************************/

    constructor(address _RCU, address _owner, address _segaFact) {

        RCU = _RCU;
        segaFactory = _segaFact;

        owner = payable(_owner);
        backupAccount = _owner;
        unlockDays = 365;
        releaseTime = block.timestamp + unlockDays * oneDay;

        SegaList.push(address(0));
    }

    receive() external payable {
        emit Received(msg.sender, msg.value);
    }

    fallback() external payable {
        if (msg.value > 0) { 
            emit Received(msg.sender, msg.value);
        }
    }

    /*********************************************
    Owner Only Functions - Relevant for backupAccount
    *********************************************/

    //For Owner to set Backup Account
    function setBackup(address _backup) public isOwner {
        _notZero(_backup);
        backupAccount = _backup;
        emit ChangedBackup(_backup);
    }

    //For Owner to set days after which Owner account declared inactive
    function setUnlockPeriod(uint256 _unlockTime) public isOwner {
        unlockDays = _unlockTime;
        ownerActive();
        emit ChangedInactiveTime(_unlockTime);
    }

    //For Backup Account to claim the role of Owner
    function claimVault() public {
        require(msg.sender == backupAccount,noAccess);
        require(block.timestamp >= releaseTime,"Owner is not declared inactive");
        address oldAc = owner;
        owner = payable(backupAccount);
        emit OwnershipTransfered(owner, oldAc);
        ownerActive();
    }

    /*------------------------------------------
    Vault Admin & Management Functions - Owner & ROs
    ------------------------------------------*/

    /********************************************
    Functions to Return Vault Assets to Owner/Sega
    *********************************************/

    //Transfering Eth to allowed counterparties
    function sendAssets(address payable receiver, uint256 amount) public isOwner {
        _checkReceiver(receiver);
        require(address(this).balance >= amount, lowBalance);
        (bool success, ) = receiver.call{value: amount}("");
        require(success, txnFail);
        emit SentOutAssets(receiver, address(0), amount);
    }

    // Transferring & Approving allowed Tokens to Owner or Sega
    function sendAssets(address receiver, address _token, uint256 amount) public isOwner {
        _notZero(_token);
        _checkReceiver(receiver);
        IERC20 Token = IERC20(_token);
        _enoughBalance(Token, amount);
        bool success = Token.transfer(receiver, amount);
        require(success, txnFail);
        emit SentOutAssets(receiver, _token, amount);
    }

    /*-------------------------------------------
    Functions to Manage Segregated Accounts
    --------------------------------------------*/

    /*********************
    Create New SEGA
    *********************/

    //For Owner to create new Segregated Account
    function createNewSega() public isOwner returns (address) {
        require(IRCU(RCU).verifySF(segaFactory));
        address _sega = ISegaFactP(segaFactory).createSega(address(this), owner);
        itemIn(_sega, SegaList, SegaMap);
        emit NewSegaCreated(_sega);
        return _sega;
    }

    //For Owner to Custom Sega Account
    function createCustomSega(address _segaFactory, uint256 index) public isOwner returns (address) {
        require(IRCU(RCU).verifySF(_segaFactory));
        address _sega = ISegaFactP(_segaFactory).createSega(address(this), owner, index);
        itemIn(_sega, SegaList, SegaMap);
        emit NewSegaCreated(_sega);
        return _sega;
    }

    /*********************
    SEGA Admin Functions
    *********************/

    //-----To Pause & Unpause Account Transfers-----//

    function pauseSega(address _sega) public isOwner isSega(_sega) {
        ISega(_sega).pauseTransfers();
        emit SegaFrozen(_sega, 1);
    }
    function unpauseSega(address _sega) public isOwner isSega(_sega) {
        ISega(_sega).unpauseTransfers();
        emit SegaFrozen(_sega, 0);
    }

    function changeTrader(address _sega, address _newTrader) public isOwner isSega(_sega) {
        ISega(_sega).changeTrader(_newTrader);
        emit SegaTraderChanged(_sega, _newTrader);
    }

    /****************************
    Force Recall of Sega Balances
    *****************************/

    function RecallFromSega(address _sega, uint256 _amount) public isOwner isSega(_sega) {
        ISega(_sega).returnToVault(_amount);
        emit SegaRecall(_sega, address(0), _amount);
    }

    function RecallFromSega(address _sega, address _token, uint256 _amount) public isOwner isSega(_sega) {
        _notZero(_token);
        ISega(_sega).returnToVault(_token, _amount);
        emit SegaRecall(_sega, _token, _amount);
    }    

    /*------------------------------------------
    Internal Functions
    -------------------------------------------*/


    function itemIn(address _item, address[] storage _list, mapping(address=>uint) storage _map) internal {
        if (_map[_item] != 0 ) {
            require( _list[_map[_item]] == address(0), "Already Enabled");
            _list[ _map[_item] ] = _item;
        } else {
        _map[_item] = _list.length;
        _list.push(_item);
        }
    }

    function checkTxn(bool success) internal pure {
        require(success, "Transfer Unsuccesful");
    }


    /*------------------------------------------
    View Only Functions
    -------------------------------------------*/

    //For Owner or Backup Account to view the Backup Account address
    function getRecoveryInfo() public view returns (address, address, uint256, uint256) {
        return (owner, backupAccount, releaseTime, unlockDays);
    }

    //For Owner to view List of Linked Sega(s)
    function viewSegaList() public view returns (address[] memory) {
        return SegaList;
    }

}

interface IRCU{
    function verifySF(address) external returns (bool);
}
